import React, { Component } from "react";
import "./TodoItem.css";

// props dùng để truyền data từ component App vào trong component con (ví dụ như component Todolist)

class TodoItem extends Component {
  // constructor(props) {
  //   super(props);
  //   this.onItemClick = this.onItemClick.bind(this);
  // }

  // onItemClick() {
  //   // console.log(this.props.item);
  //   this.props.item.isComplete = !this.props.item.isComplete;
  // }

  render() {
    const { item } = this.props; // tương đương item = this.props.item, gán item bằng thís.props.item
    let className = "TodoItem";
    if (item.isComplete) {
      className += " TodoItem-complete";
    }
    return (
      <div
        onClick={this.onItemClick}
        className={className("TodoItem", {
          "TodoItem-complete": item.isComplete
        })}
      >
        {/* <p > Đi chợ </p>  */}
        <p>{this.props.item.title}</p>
      </div>
    );
  }
}

export default TodoItem;
