import React, { Component } from "react";
// import logo from "./logo.svg";
import "./App.css";
import TodoItem from "./components/TodoItem";

class App extends Component {
  constructor() {
    super();
    this.state = {
      todoItems: [
        { title: "Đi đá bóng", isComplete: true }, // thêm title để gía trị chuyển vào linh hoat hơn, k chỉ là string nữa
        { title: "Đi chơi", isComplete: true },
        { title: "Đi đổ xăng" },
      ],
    };
  }
  onItemClicked() {
    return (event) => {
      const isComplete = item.isComplete;
      const { todoItems } = this.state;
      const index = todoItems.indexOf(item);
      this.setState({
        todoItems: [
          ...todoItems.slice(0, index),
          {
            ...item,
            isComplete: !isComplete
          },
          ...todoItems.slice(index + 1)
        ],
      });
    };
  }
  render() {
    const { todoItems } = this.state;
    if (todoItems.length) {
      // Cách 2
      return (
        <div className="App">
          {this.todoItems.length > 0 && // có && là viết tắt của nếu cái trc && đúng thì chạy cái đằng sau &&
            this.todoItems.map((item, index) => (
              <TodoItem key={index} item={item} onClick={this.onItemClicked} />
            ))}
          {this.todoItems.length == 0 && "Nothing here"}
        </div>
      );
    }
  }
}

export default App;

