import React, { Component } from "react";
// import logo from "./logo.svg";
import "./App.css";
import TodoItem from "./components/TodoItem";

class App extends Component {
  constructor() {
    super();
    this.todoItems = [
      // { title: "Đi đá bóng", isComplete: true }, // thêm title để gía trị chuyển vào linh học hơn, k chỉ là string nữa
      // { title: "Đi chơi" },
      // { title: "Đi đổ xăng" },
    ];
  }
  render() {
    if (this.todoItems.length > 0) {
      return (
        <div className="App">
          {this.todoItems.map((item, index) => (
            <TodoItem key={index} item={item} />
          ))}

          {/**
           * map biến array của object thành array của 1 component thì phải thêm key
           * map nhận vào hai giá trị
           * chuyển sang cho 1 component
           */}

          {/* <TodoItem title="Đi đá bóng" />
          <TodoItem title="Đi chơi" />
          <TodoItem title="Đi đổ xăng" /> */}
        </div>
      );
    }
    else {
      return (
        <div className = "App">
          "Nothing here"
        </div>
      );
    }
  }
}

export default App;
