import React, { Component } from "react";
import "./TodoItem.css";

// props dùng để truyền data từ component App vào trong component con (ví dụ như component Todolist)

class TodoItem extends Component {

  onItemClick() {
    console.log(this.props.item);
  }

  render() {
    const { item } = this.props; // tương đương item = this.props.item, gán item bằng thís.props.item
    let className = "TodoItem";
    if (item.isComplete) {
        className += " TodoItem-complete";
    }
    return (
      <div onClick = {this.onItemClick} className={className}>
        {/* <p > Đi chợ </p>  */}
        <p>{this.props.item.title}</p>
      </div>
    );
  }
}

export default TodoItem;
